$('.Search .WikiaSearch input[name="search"]').attr("placeholder", "Search this wiki");

/*External Links - by ShermanTheMythran*/
$('.links a, .wikis a, a.external, .WikiNav > ul > li:last-child > .subnav-2 > li:first-child > .subnav-2a, .WikiNav > ul > li:last-child > .subnav-2 > li > .subnav-3 > li > .subnav-3a').attr('target', '_blank');

/*Fix Tabber - by ShermanTheMythran*/
$(document).ready(function(){
	$('.tabbernav li a').removeAttr('title');
});

/*Button Hub - by ShermanTheMythran*/
$('.WikiaHeader').append('<span id="button-hub" style="position: fixed; top: 0; margin-top: -14px; right: 20px; transition: margin .5s; -moz-transition: margin .5s; -webkit-transition: margin .5s; -o-transition: margin .5s;"><span class="button" id="scroll-top" title="Scroll to top" style="border-radius: 0 0 0 100% / 0 0 0 10px; -moz-border-radius: 0 0 0 100% / 0 0 0 10px; -webkit-border-radius: 0 0 0 100% / 0 0 0 10px;">▲</span>' + '<span class="button" id="remove-ads" title="Hide ads" style="border-radius: 0; -moz-border-radius: 0; -webkit-border-radius: 0; border-left: 1px solid silver !important; border-right: 1px solid silver !important;">●</span>' + '<span class="button" id="scroll-bottom" title="Scroll to bottom" style="border-radius: 0 0 100% 0 / 0 0 10px 0; -moz-border-radius: 0 0 100% 0 / 0 0 10px 0; -webkit-border-radius: 0 0 100% 0 / 0 0 10px 0;">▼</span>');

$('#scroll-top').click(function() { $('html, body').animate({scrollTop:0}, 'slow') });

$('#remove-ads').toggle(function() {
	$('.wikia-ad, .SelfServeUrl').hide('slow');
	$('#remove-ads').attr('title', 'Show ads'); },
	function() {
		$('.wikia-ad, .SelfServeUrl').show('slow');
		$('#remove-ads').attr('title', 'Hide ads');
	}
);

$('#scroll-bottom').click(function() { $('html, body').animate({scrollTop:$(document).height()}, 'slow') });

/*Hide Rail - by ShermanTheMythran*/
$('.WikiaBarWrapper .tools').append('<li id="rail-button" title="Hide rail" style="cursor: pointer; font-size: 16px; transition: transform .5s, text-shadow .5s; -moz-transition: -moz-transform .5s, text-shadow .5s; -webkit-transition: -webkit-transform .5s, text-shadow .5s; -o-transition: -o-transform .5s, text-shadow .5s;"><a>»</a></li>');

$('#rail-button').toggle(function() {
	$('.WikiaRail').hide('slow');
	$('.WikiaMainContent').animate({width:'1010'}, 'slow');
	$('#rail-button').css({'transform':'rotateY(180deg)','-moz-transform':'rotateY(180deg)','-webkit-transform':'rotateY(180deg)','-ms-transform':'scaleX(-1)','-o-transform':'scaleX(-1)'});
	$('#rail-button').attr('title', 'Show rail'); },
	function() {
		$('.WikiaRail').show('slow');
		$('.WikiaMainContent').animate({width:'670'}, 'slow');
		$('#rail-button').css({'transform':'rotateY(0deg)','-moz-transform':'rotateY(0deg)','-webkit-transform':'rotateY(0deg)','-ms-transform':'scaleX(1)','-o-transform':'scaleX(1)'});
		$('#rail-button').attr('title', 'Hide rail');
	}
);

